```java:src/main/java/com/example/group15chainmeatproducer/Saiful/User3/QAHACCPComplianceController.java, lines=ALL(1-136)
package com.example.group15chainmeatproducer.Saiful.User3;

import com.example.group15chainmeatproducer.SceneManager;
import com.example.group15chainmeatproducer.Saiful.User3.models.HACCPCompliance;
import com.example.group15chainmeatproducer.Saiful.utils.DataManager;
import javafx.event.ActionEvent;
import javafx.fxml.FXML;
import javafx.scene.control.*;
import javafx.scene.control.cell.PropertyValueFactory;

import java.time.LocalDate;
import java.util.ArrayList;

public class QAHACCPComplianceController {

 @FXML
 private ComboBox<String> controlPointComboBox;
 @FXML
 private DatePicker monitorDatePicker;
 @FXML
 private RadioButton compliantRadio;
 @FXML
 private RadioButton nonConformityRadio;
 @FXML
 private TextField observationField;
 @FXML
 private TextField parametersField;
 @FXML
 private TableView<HACCPCompliance> haccpTable;
 @FXML
 private TableColumn<HACCPCompliance, String> controlPointColumn;
 @FXML
 private TableColumn<HACCPCompliance, String> statusColumn;
 @FXML
 private TableColumn<HACCPCompliance, LocalDate> dateColumn;
 @FXML
 private TableColumn<HACCPCompliance, String> notesColumn;
 @FXML
 private Button backButton;
 @FXML
 private Button submitComplianceButton;
 @FXML
 private Button flagNonConformityButton;

 private ToggleGroup complianceGroup;
 private ArrayList<HACCPCompliance> haccpCompliances;

 @FXML
 private void initialize() {
 complianceGroup = new ToggleGroup();
 compliantRadio.setToggleGroup(complianceGroup);
 nonConformityRadio.setToggleGroup(complianceGroup);

 controlPointComboBox.getItems().addAll(
 "Temperature Control", "pH Monitoring", "Metal Detection", "Pathogen Testing",
 "Cleaning Validation", "Water Quality", "Pest Control", "Storage Conditions"
 );

 controlPointColumn.setCellValueFactory(new PropertyValueFactory<>("controlPoint"));
 statusColumn.setCellValueFactory(new PropertyValueFactory<>("status"));
 dateColumn.setCellValueFactory(new PropertyValueFactory<>("monitorDate"));
 notesColumn.setCellValueFactory(new PropertyValueFactory<>("notes"));

 loadHACCPCompliances();
 refreshTable();
 }

 private void loadHACCPCompliances() {
 haccpCompliances = (ArrayList<HACCPCompliance>) DataManager.loadFromFile("haccp_compliances");
 }

 private void saveHACCPCompliances() {
 DataManager.saveToFile(haccpCompliances, "haccp_compliances");
 }

 private void refreshTable() {
 haccpTable.getItems().clear();
 haccpTable.getItems().addAll(haccpCompliances);
 }

 @FXML
 private void handleBack(ActionEvent event) {
 SceneManager.switchToUser3Menu(event);
 }

 @FXML
 private void handleSubmitCompliance(ActionEvent event) {
 String controlPoint = controlPointComboBox.getValue();
 LocalDate date = monitorDatePicker.getValue();
 RadioButton selectedRadio = (RadioButton) complianceGroup.getSelectedToggle();
 String status = selectedRadio != null ? selectedRadio.getText() : "";
 String observation = observationField.getText();
 String parameters = parametersField.getText();

 if (controlPoint != null && date != null && !status.isEmpty() && !observation.isEmpty() && !parameters.isEmpty()) {
 HACCPCompliance compliance = new HACCPCompliance(controlPoint, status, date, observation, parameters, "Compliance monitored");
 haccpCompliances.add(compliance);
 saveHACCPCompliances();
 refreshTable();
 clearForm();
 showAlert("Success", "HACCP compliance submitted successfully!");
 } else {
 showAlert("Error", "Please fill in all fields!");
 }
 }

 @FXML
 private void handleFlagNonConformity(ActionEvent event) {
 HACCPCompliance selectedCompliance = haccpTable.getSelectionModel().getSelectedItem();
 if (selectedCompliance != null) {
 selectedCompliance.setStatus("Non-Conformity");
 selectedCompliance.setNotes("Flagged for non-conformity - requires immediate action");
 saveHACCPCompliances();
 refreshTable();
 showAlert("Warning", "Non-conformity flagged for control point: " + selectedCompliance.getControlPoint());
 } else {
 showAlert("Error", "Please select a compliance record to flag!");
 }
 }

 private void clearForm() {
 controlPointComboBox.setValue(null);
 monitorDatePicker.setValue(null);
 complianceGroup.selectToggle(null);
 observationField.clear();
 parametersField.clear();
 }

 private void showAlert(String title, String message) {
 Alert alert = new Alert(Alert.AlertType.INFORMATION);
 alert.setTitle(title);
 alert.setHeaderText(null);
 alert.setContentText(message);
 alert.showAndWait();
 }
}
