package com.example.group15chainmeatproducer.Saiful.User3;

import com.example.group15chainmeatproducer.SceneManager;
import com.example.group15chainmeatproducer.Saiful.User3.models.FinalProductBatch;
import com.example.group15chainmeatproducer.Saiful.utils.DataManager;
import javafx.event.ActionEvent;
import javafx.fxml.FXML;
import javafx.scene.control.*;
import javafx.scene.control.cell.PropertyValueFactory;

import java.time.LocalDate;
import java.util.ArrayList;

public class QAFinalProductApprovalController {

 @FXML
 private ComboBox<String> productBatchComboBox;
 @FXML
 private DatePicker reviewDatePicker;
 @FXML
 private RadioButton approvedRadio;
 @FXML
 private RadioButton rejectedRadio;
 @FXML
 private TextField qualityRecordsField;
 @FXML
 private TextField decisionNotesField;
 @FXML
 private TableView<FinalProductBatch> finalProductTable;
 @FXML
 private TableColumn<FinalProductBatch, String> batchIdColumn;
 @FXML
 private TableColumn<FinalProductBatch, String> statusColumn;
 @FXML
 private TableColumn<FinalProductBatch, LocalDate> dateColumn;
 @FXML
 private TableColumn<FinalProductBatch, String> decisionColumn;
 @FXML
 private Button backButton;
 @FXML
 private Button submitApprovalButton;
 @FXML
 private Button notifyDispatchButton;

 private ToggleGroup approvalGroup;
 private ArrayList<FinalProductBatch> dataList;

 @FXML
 private void initialize() {
 setupRadioButtons();
 setupComboBox();
 setupTableView();
 loadData();
 refreshTable();
 }

 private void setupRadioButtons() {
 approvalGroup = new ToggleGroup();
 approvedRadio.setToggleGroup(approvalGroup);
 rejectedRadio.setToggleGroup(approvalGroup);
 }

 private void setupComboBox() {
 productBatchComboBox.getItems().addAll(
 "PROD-2024-001", "PROD-2024-002", "PROD-2024-003", "PROD-2024-004", "PROD-2024-005",
 "PROD-2024-006", "PROD-2024-007", "PROD-2024-008", "PROD-2024-009", "PROD-2024-010"
 );
 }

 private void setupTableView() {
 batchIdColumn.setCellValueFactory(new PropertyValueFactory<>("batchId"));
 statusColumn.setCellValueFactory(new PropertyValueFactory<>("status"));
 dateColumn.setCellValueFactory(new PropertyValueFactory<>("reviewDate"));
 decisionColumn.setCellValueFactory(new PropertyValueFactory<>("decision"));
 }

 private void loadData() {
 dataList = (ArrayList<FinalProductBatch>) DataManager.loadFromFile("final_product_data");
 }

 private void saveData() {
 DataManager.saveToFile(dataList, "final_product_data");
 }

 private void refreshTable() {
 finalProductTable.getItems().clear();
 finalProductTable.getItems().addAll(dataList);
 }

 @FXML
 private void handleBack(ActionEvent event) {
 SceneManager.switchToUser3Menu(event);
 }

 @FXML
 private void handleSubmitApproval(ActionEvent event) {
 String batchId = productBatchComboBox.getValue();
 LocalDate date = reviewDatePicker.getValue();
 RadioButton selected = (RadioButton) approvalGroup.getSelectedToggle();
 String status = selected != null ? selected.getText() : "";
 String qualityRecords = qualityRecordsField.getText();
 String decisionNotes = decisionNotesField.getText();

 if (isValidInput(batchId, date, status, qualityRecords, decisionNotes)) {
 FinalProductBatch newRecord = new FinalProductBatch(batchId, status, date, qualityRecords, decisionNotes, "Reviewed");
 dataList.add(newRecord);
 saveData();
 refreshTable();
 clearForm();
 showMessage("Final product approval submitted successfully!");
 } else {
 showMessage("Please fill all required fields!");
 }
 }

 @FXML
 private void handleNotifyDispatch(ActionEvent event) {
 FinalProductBatch selected = finalProductTable.getSelectionModel().getSelectedItem();
 if (selected != null && "Approved".equals(selected.getStatus())) {
 selected.setDecision("DISPATCH NOTIFIED - Ready for shipment");
 saveData();
 refreshTable();
 showMessage("Dispatch team has been notified for batch: " + selected.getBatchId());
 } else if (selected == null) {
 showMessage("Please select a product batch!");
 } else {
 showMessage("Only approved batches can be dispatched!");
 }
 }

 private boolean isValidInput(String batchId, LocalDate date, String status, String qualityRecords, String decisionNotes) {
 return batchId != null && date != null && !status.isEmpty() &&
 !qualityRecords.isEmpty() && !decisionNotes.isEmpty();
 }

 private void clearForm() {
 productBatchComboBox.setValue(null);
 reviewDatePicker.setValue(null);
 approvalGroup.selectToggle(null);
 qualityRecordsField.clear();
 decisionNotesField.clear();
 }

 private void showMessage(String message) {
 Alert alert = new Alert(Alert.AlertType.INFORMATION);
 alert.setTitle("Information");
 alert.setHeaderText(null);
 alert.setContentText(message);
 alert.showAndWait();
 }
}
