package com.example.group15chainmeatproducer.Saiful.User3;

import com.example.group15chainmeatproducer.SceneManager;
import com.example.group15chainmeatproducer.Saiful.User3.models.HygieneChecklist;
import com.example.group15chainmeatproducer.Saiful.utils.DataManager;
import javafx.event.ActionEvent;
import javafx.fxml.FXML;
import javafx.scene.control.*;
import javafx.scene.control.cell.PropertyValueFactory;
import javafx.stage.FileChooser;

import java.io.File;
import java.time.LocalDate;
import java.util.ArrayList;

public class QAHygieneChecklistController {

 @FXML
 private ComboBox<String> cleaningBatchComboBox;
 @FXML
 private DatePicker verificationDatePicker;
 @FXML
 private RadioButton completeRadio;
 @FXML
 private RadioButton incompleteRadio;
 @FXML
 private TextField photoUploadField;
 @FXML
 private TextField notesField;
 @FXML
 private TableView<HygieneChecklist> hygieneTable;
 @FXML
 private TableColumn<HygieneChecklist, String> batchIdColumn;
 @FXML
 private TableColumn<HygieneChecklist, String> statusColumn;
 @FXML
 private TableColumn<HygieneChecklist, LocalDate> dateColumn;
 @FXML
 private TableColumn<HygieneChecklist, String> photoColumn;
 @FXML
 private Button backButton;
 @FXML
 private Button uploadPhotoButton;
 @FXML
 private Button submitReportButton;

 private ToggleGroup statusGroup;
 private ArrayList<HygieneChecklist> dataList;

 @FXML
 private void initialize() {
 setupRadioButtons();
 setupComboBox();
 setupTableView();
 loadData();
 refreshTable();
 }

 private void setupRadioButtons() {
 statusGroup = new ToggleGroup();
 completeRadio.setToggleGroup(statusGroup);
 incompleteRadio.setToggleGroup(statusGroup);
 }

 private void setupComboBox() {
 cleaningBatchComboBox.getItems().clear();
 cleaningBatchComboBox.getItems().addAll(
 "BATCH-001", "BATCH-002", "BATCH-003", "BATCH-004", "BATCH-005",
 "BATCH-006", "BATCH-007", "BATCH-008", "BATCH-009", "BATCH-010"
 );
 cleaningBatchComboBox.setPromptText("Select Cleaning Batch");
 }

 private void setupTableView() {
 batchIdColumn.setCellValueFactory(new PropertyValueFactory<>("batchId"));
 statusColumn.setCellValueFactory(new PropertyValueFactory<>("status"));
 dateColumn.setCellValueFactory(new PropertyValueFactory<>("verificationDate"));
 photoColumn.setCellValueFactory(new PropertyValueFactory<>("photoPath"));
 }

 private void loadData() {
 dataList = (ArrayList<HygieneChecklist>) DataManager.loadFromFile("hygiene_data");
 }

 private void saveData() {
 DataManager.saveToFile(dataList, "hygiene_data");
 }

 private void refreshTable() {
 hygieneTable.getItems().clear();
 hygieneTable.getItems().addAll(dataList);
 }

 @FXML
 private void handleBack(ActionEvent event) {
 SceneManager.switchToUser3Menu(event);
 }

 @FXML
 private void handleUploadPhoto(ActionEvent event) {
 FileChooser fileChooser = new FileChooser();
 fileChooser.setTitle("Select Image File");
 fileChooser.getExtensionFilters().add(
 new FileChooser.ExtensionFilter("Image Files", "*.png", "*.jpg", "*.jpeg", "*.gif")
 );

 File selectedFile = fileChooser.showOpenDialog(uploadPhotoButton.getScene().getWindow());
 if (selectedFile != null) {
 photoUploadField.setText(selectedFile.getName());
 showMessage("Photo Selected: " + selectedFile.getName());
 }
 }

 @FXML
 private void handleSubmitReport(ActionEvent event) {
 String batchId = cleaningBatchComboBox.getValue();
 LocalDate date = verificationDatePicker.getValue();
 RadioButton selected = (RadioButton) statusGroup.getSelectedToggle();
 String status = selected != null ? selected.getText() : "";
 String photoPath = photoUploadField.getText();
 String notes = notesField.getText();

 if (isValidInput(batchId, date, status, notes)) {
 HygieneChecklist newRecord = new HygieneChecklist(batchId, status, date, photoPath, notes);
 dataList.add(newRecord);
 saveData();
 refreshTable();
 clearForm();
 showMessage("Hygiene checklist submitted successfully!");
 } else {
 showMessage("Please fill all required fields!");
 }
 }

 private boolean isValidInput(String batchId, LocalDate date, String status, String notes) {
 return batchId != null && date != null && !status.isEmpty() && !notes.isEmpty();
 }

 private void clearForm() {
 cleaningBatchComboBox.setValue(null);
 verificationDatePicker.setValue(null);
 statusGroup.selectToggle(null);
 photoUploadField.clear();
 notesField.clear();
 }

 private void showMessage(String message) {
 Alert alert = new Alert(Alert.AlertType.INFORMATION);
 alert.setTitle("Information");
 alert.setHeaderText(null);
 alert.setContentText(message);
 alert.showAndWait();
 }
}
