 new Product(null, "Chicken Breast", 7.49, "Lean and versatile"),
 new Product(null, "Lamb Chops", 15.25, "Premium lamb cuts")
 );

 // Set initial items
 productTable.setItems(masterData);

 // Simple search filter without FilteredList/SortedList
 searchField.textProperty().addListener((obs, oldV, newV) -> {
 String q = newV == null ? "" : newV.trim().toLowerCase();
 if (q.isEmpty()) {
 productTable.setItems(masterData);
 return;
 }
 ObservableList<Product> filtered = FXCollections.observableArrayList();
 for (Product p : masterData) {
 if ((p.getName() != null && p.getName().toLowerCase().contains(q)) ||
 (p.getShortDescription() != null && p.getShortDescription().toLowerCase().contains(q))) {
 filtered.add(p);
 }
 }
 productTable.setItems(filtered);
 });
 }

